import React, { useEffect, useState } from "react";
import {
  Link,
  Switch,
  Route,
  useLocation,
  useParams,
  useHistory,
  useRouteMatch,
} from "react-router-dom";

export default function Display() {

  

  const history = useHistory();

  const [decks, setDecks] = useState([]);

  useEffect(() => {
    listDecks().then((decks) => {
